mapper.select:
SELECT COUNT(1)
FROM apply_job AS aj
LEFT JOIN job AS j ON aj.job_id = j.id AND j.is_delete = 0

    j.uid = #{userId}

        AND j.position_code = #{listDto.positionCode}

    AND aj.is_delete = 0

SELECT aj.id, aj.applicant, u.avatar, nv.last_name AS lastName, nv.first_name AS firstName, u.gender, u.personal_signature AS personalSignature, aj.recruit_process_instance_id AS recruitProcessInstanceId, p.name AS positionName, j.min_salary AS minSalary, j.max_salary AS maxSalary, aj.create_time AS applyTime FROM apply_job AS aj LEFT JOIN job AS j ON aj.job_id = j.id AND j.is_delete = 0 LEFT JOIN position AS p ON j.position_code = p.code AND p.is_delete = 0 LEFT JOIN yy_user AS u ON aj.applicant = u.id AND u.is_delete = 0 LEFT JOIN yy_user_name_visible_info AS nv ON u.id = nv.uid AND nv.is_delete = 0
j.uid = #{userId}
AND j.position_code = #{listDto.positionCode}
AND aj.is_delete = 0 ORDER BY aj.create_time DESC LIMIT #{listDto.pageSize} OFFSET (#{listDto.pageNumber} -1) * #{listDto.pageSize}
